package com.assignment1;

public class Task_7_Pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 5;
		int j = 5;
		for (i = 0; i <= 5; i++) {

			for (j = 0; j <= i; j++) {
				System.out.print("* ");
			}
			System.out.println();
		}

	}

}
